mapper.insert:
insert into sitter_info( user_no
						,file_no
						,pet_kind
						,work_day
						,service
						,zipcode
						,addr
						,addr_detail
						,sitter_info
) values(#{user_no, jdbcType=INTEGER}
		,#{file_no, jdbcType=INTEGER}
		,#{pet_kind, jdbcType=VARCHAR}
		,#{work_day, jdbcType=VARCHAR}
		,#{service, jdbcType=VARCHAR}
		,#{zipcode, jdbcType=INTEGER}
		,#{addr, jdbcType=VARCHAR}
		,#{addr_detail, jdbcType=VARCHAR}
		,#{sitter_info, jdbcType=VARCHAR}
)

mapper.select:
SELECT service_no ,cs.user_no ,cs.pet_no ,pet_kind ,cs.zipcode ,cs.addr ,cs.addr_detail ,service ,TO_CHAR(st_date,'YYYY/MM/DD') st_date ,TO_CHAR(end_date,'YYYY/MM/DD') end_date ,request_money ,request_title ,request_detail ,status ,TO_CHAR(reg_date,'YYYY/MM/DD') reg_date ,cs.file_no ,nickname ,u.gender FROM care_service cs inner join PET_INFO pif on cs.pet_no = pif.pet_no inner join users u on cs.user_no = u.user_no
cs.zipcode LIKE #{zipcode}||'%'
and st_date >= #{st_date}
and end_date <= #{end_date}
and st_date >= #{st_date} and end_date <= #{end_date}
service LIKE '%'||#{item}||'%'
pet_kind LIKE '%'||#{item}||'%'
u.gender LIKE '%'||#{item}||'%'

mapper.update:

update users
set file_no = #{file_no}
where user_no = #{user_no}
