mapper.select:

	select *
	from users
	where name=#{name,jdbcType=VARCHAR} and email=#{email}

mapper.update:

   update users 
   set password = #{password,jdbcType=VARCHAR}
   where id = #{id,jdbcType=VARCHAR}
   and email = #{email}

mapper.select:
 
	select * 
	from users 
	where id = #{id} and password = #{password}


select *
from users 
where nickname=#{nickname} and email=#{email}
